import React from 'react';
import { View, Text, Button, StyleSheet, TouchableOpacity, Image } from 'react-native';

function OrderDetails ({route, navigation}) {
  const tripInfo = route.params.otherParam;  
  console.log(tripInfo)
  React.useLayoutEffect(() => {
    navigation.setOptions({
      headerLeft: () => (
        <TouchableOpacity style={{flex:1, flexDirection: 'row'}} onPress={() => navigation.goBack()}>
          <Image source={require('../assets/images/back-arrow.png')} style={{ width: 28,resizeMode: 'center', height: 28}}/>
          <Text style={{color: "#c8c8c8", paddingLeft: 10, paddingTop: 2, fontSize: 18}}>Back to Trip List</Text>
        </TouchableOpacity>
      )
    });
  }, [navigation]);
    return (
      <View style={styles.container}> 
        <View style={styles.tripHeader}> 
          <Text style={styles.mainText}>TRIP NUMBER - {tripInfo.tripID}</Text>
          <Text style={styles.title}>Tracking : {item.trackingStatus}</Text>
        </View>
        <View style={styles.tripList}>
          <View style={{flex: 2}}>
            <Text style={styles.triplabel}>From</Text>
            <Text style={styles.tripname}>{item.tripInfo.dropOff}</Text>
            <Text style={styles.datelabel}>Last Drop Off</Text>
            <Text style={styles.dateText}>{item.tripInfo.dropOffDate}</Text>
          </View>
          <View style={{flex: 2}}>
            <Text style={styles.triplabel}>To</Text>
            <Text style={styles.tripname}>{item.tripInfo.desVal}</Text>
            <Text style={styles.datelabel}>Est. Arrival</Text>
            <Text style={styles.dateText}>{item.tripInfo.pickUpDate}</Text>
          </View>
          <View style={{flex: 2 ,flexDirection: "row",justifyContent: "center", alignItems:"center" }}>
            <Text style={styles.title}>{item.packageLists}</Text>
            <Image source={require('../assets/images/Supplier.png')} style={{ width: 28,resizeMode: 'center', height: 20 }}/>
          </View>
        </View> 
      </View>
    );
};

export default OrderDetails;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginHorizontal: 20 
  },
  mainText: {
    fontSize: 20,
    fontFamily: 'UbuntuBold',
    paddingLeft: 5, 
  },
});
