        <Dropdown
            style={[styles.dropdown, isFocus && { borderColor: 'blue' }]}
            placeholderStyle={styles.placeholderboldStyle}
            selectedTextStyle={styles.selectedTextStyle} 
            iconStyle={styles.iconStyle}
            data={prohibitedData} 
            fontFamily='Ubuntu' 
            maxHeight={200}  
            activeColor="#D9D9D9"
            labelField="category"
            valueField="value"
            placeholder={!isFocus ? 'Add prohibited item' : '...'}
            searchPlaceholder="Search..."
            value={value}
            onFocus={() => setIsFocus(true)}
            onBlur={() => setIsFocus(false)}
            onChange={item => {
              setValue(item.value);
              setCategory(item.category)
              setCategoryItem(item.itemname)
              setIsFocus(false);
            }}
            renderLeftIcon={() => (
              <FontAwesome
                style={styles.icon}
                color={isFocus ? 'blue' : 'black'}
                name="ban"
                size={23}
              />
            )}
            renderRightIcon={() => (
              <FontAwesome
                style={styles.icon}
                color={isFocus ? 'blue' : 'black'}
                name="chevron-down"
                size={23}
              />
            )}
        /> 
        <View style={{ flex: 1 }}> 
          <Text style={styles.inputLabel}>Prohitbited item</Text>
          <TextInput style={styles.input} defaultValue={category} placeholder="Custom Item"/>
        </View>  

        <TouchableOpacity style={styles.dateList} onPress={addList} > 
                <FontAwesome
                  style={styles.icon}
                  name="plus-circle"
                  size={23}
                />
               <Text style={styles.label}> Add </Text> 
        </TouchableOpacity>
      <View style={{ flex: 1, flexDirection: "row", justifyContent: 'center', marginTop: 15}}> 
        <TouchableOpacity onPress={() => navigation.navigate('Home')} >
            <Image source={require('../assets/images/createTripBtn.png')} style={{ width: 316,resizeMode: 'center', height: 45}}/>
          </TouchableOpacity>
      </View>
    </View>
    </ScrollView>
  );
};

